import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Container,
  Box,
  Paper,
  Avatar,
  Typography,
  Button,
  Grid,
  CircularProgress,
} from '@mui/material';
import Post from '../components/Post';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';

function Profile() {
  const { username } = useParams();
  const { user: currentUser } = useAuth();
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchUserData = async () => {
    try {
      const [userResponse, postsResponse] = await Promise.all([
        axios.get(`http://localhost:5000/api/users/${username}`),
        axios.get(`http://localhost:5000/api/posts/user/${username}`),
      ]);
      setUser(userResponse.data);
      setPosts(postsResponse.data);
    } catch (error) {
      console.error('Error fetching user data:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUserData();
  }, [username]);

  const handleFollow = async () => {
    try {
      await axios.post(`http://localhost:5000/api/users/${username}/follow`);
      fetchUserData();
    } catch (error) {
      console.error('Error following user:', error);
    }
  };

  if (loading) {
    return (
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          minHeight: '60vh',
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  if (!user) {
    return (
      <Container>
        <Typography variant="h5" sx={{ mt: 4 }}>
          User not found
        </Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="md">
      <Paper sx={{ mt: 3, p: 3 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
          <Avatar
            src={user.profilePicture}
            sx={{ width: 120, height: 120, mr: 3 }}
          >
            {user.username[0].toUpperCase()}
          </Avatar>
          <Box>
            <Typography variant="h4" gutterBottom>
              {user.username}
            </Typography>
            <Typography variant="body1" color="text.secondary" gutterBottom>
              {user.bio || 'No bio yet'}
            </Typography>
            <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
              <Typography>
                <strong>{user.followers.length}</strong> followers
              </Typography>
              <Typography>
                <strong>{user.following.length}</strong> following
              </Typography>
            </Box>
            {currentUser._id !== user._id && (
              <Button
                variant="contained"
                onClick={handleFollow}
                color={
                  user.followers.includes(currentUser._id)
                    ? 'secondary'
                    : 'primary'
                }
              >
                {user.followers.includes(currentUser._id)
                  ? 'Unfollow'
                  : 'Follow'}
              </Button>
            )}
          </Box>
        </Box>
      </Paper>

      <Box sx={{ mt: 4 }}>
        <Typography variant="h6" gutterBottom>
          Posts
        </Typography>
        <Grid container spacing={2}>
          {posts.map((post) => (
            <Grid item xs={12} key={post._id}>
              <Post post={post} onUpdate={fetchUserData} />
            </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
  );
}

export default Profile;
